import FormData from 'form-data';

import { createReadStream } from 'fs';

import { parse } from 'url';

import { request } from 'http';

import {request as requestS} from 'https';


/**
 * @external {Context} https://github.com/koajs/koa/blob/master/docs/api/context.md
 */


/**
 * @param {Context} context
 *
 * @return {FormData|Object|string}
 */
function buildData(context) {

    if (/^Head|Get|Delete$/.test( context.method ))  return '';

    const request = context.request;

    if (request.type !== 'multipart/form-data')  return request.body;

    const form = new FormData();

    for (let key in request.fields)  form.append(key, request.fields[ key ]);

    for (let key in request.files)
        for (let file  of  request.files[ key ])
            form.append(key,  createReadStream( file.path ),  file.name);

    return form;
}


/**
 * HTTP(S) request
 *
 * @param {string|URL}           URL
 * @param {string}               [method='GET']
 * @param {Object}               [header]
 * @param {string|Object|Buffer} [body]
 *
 * @return {Promise<IncomingMessage>} HTTP(S) response
 */
export function resourceFrom(URL,  method = 'GET',  header,  body) {

    if (typeof URL === 'string')  URL = parse( URL );

    const option = {
            method,
            headers:  Object.assign({
                Accept:        '*/*',
                'User-Agent':  `Node.JS ${process.version}`
            }, header)
        },
        client = (URL.protocol[4] !== 's') ? request : requestS;

    if ((body instanceof Object)  &&  !(body instanceof Buffer)) {

        option.headers['Content-Type'] = 'application/json';

        body = JSON.stringify( body );
    }

    for (let key in URL)
        if (URL[ key ]  &&  !(URL[key] instanceof Function))
            option[key] = URL[key];

    return  new Promise((resolve, reject) =>
        client(option, resolve).on('error', reject).end( body )
    );
}


/**
 * @param {string}  URL
 * @param {Context} context
 *
 * @return {IncomingMessage}
 */
async function pipe(URL, context) {

    const header = Object.assign({ },  context.header);

    delete header.host;

    const response = await resourceFrom(
        URL,  context.method,  header,  buildData( context )
    );

    context.status = response.statusCode,
    context.message = response.statusMessage;

    for (let key in response.headers)  if (key !== 'status')
        context.set(
            key.replace(/^\w|-\w/g,  char => char.toUpperCase()),
            response.headers[ key ]
        );

    return  context.body = response;
}


/**
 * @param {Object} proxyMap
 *
 * @return {AsyncFunction} Koa middleware
 */
export default  function (proxyMap) {
    /**
     * @param {Context}  context
     * @param {Function} next
     *
     * @return {?IncomingMessage}
     */
    return  async function (context, next) {

        const URL = context.path;

        for (let path in proxyMap) {

            let final = URL.replace(proxyMap[ path ],  path);

            if (final !== URL)  return pipe(final, context);
        }

        await next();
    };
}

import { readFileSync } from 'fs';


/**
 * Get configuration of a Package from `package.json` in `process.cwd()`
 *
 * @param {string} name
 *
 * @return {?Object} (`process.env.NODE_ENV` will affect the result)
 */
export function configOf(name) {

    const config = JSON.parse( readFileSync('./package.json') )[ name ];

    if ( config )
        return  config.env  ?  config.env[ process.env.NODE_ENV ]  :  config;
}


/**
 * @param {Object} map - Key for RegExp source, value for replacement
 *
 * @return {?Object} Key for replacement, value for RegExp
 */
export function patternOf(map) {

    var patternMap = { }, count = 0;

    for (let pattern in map)
        patternMap[ map[ pattern ] ] = new RegExp( pattern ),  count++;

    return  count ? patternMap : null;
}


/**
 * @return {string}
 */
export function currentModulePath() {

    try {  throw Error();  } catch (error) {

        return  error.stack.split( /[\r\n]+/ )[2]
            .match( /at .+?\((.+):\d+:\d+\)/ )[1];
    }
}

import 'babel-polyfill';

import Koa from 'koa';

import { patternOf, currentModulePath } from './utility';

import Logger from 'koa-logger';

import Body from 'koa-body';

import ProxyAgent from './ProxyAgent';

import CORS from '@koa/cors';

import Static from 'koa-static';

import IP from 'internal-ip';

import open from 'opn';

import { resolve } from 'url';

import { join } from 'path';

import { fork } from 'child_process';


/**
 * Web server
 */
export default  class WebServer {
    /**
     * @param {string}         [staticPath='.']
     * @param {number}         [netPort=0]
     * @param {boolean}        [XDomain=false]
     * @param {?Object}        proxyMap         - Same as the parameter of {@link patternOf}
     * @param {boolean|string} [openURL=false]
     */
    constructor(staticPath, netPort, XDomain, proxyMap, openURL) {
        /**
         * @type {string}
         */
        this.staticPath = staticPath || '.';

        /**
         * @type {number}
         */
        this.netPort = +(netPort || 0);

        /**
         * @private
         *
         * @type {Application}
         */
        this.core = new Koa();

        /**
         * @type {boolean}
         */
        this.XDomain = XDomain;

        /**
         * @private
         *
         * @type {?Object}
         */
        this.proxyMap = patternOf( proxyMap );

        /**
         * @private
         *
         * @type {boolean|string}
         */
        this.openPath = openURL;

        /**
         * @private
         *
         * @type {ServerAddress}
         */
        this.address = null;

        this.boot();
    }

    /**
     * @private
     */
    boot() {

        this.core.use( Logger() );

        if ( this.proxyMap )
            this.core.use(
                Body({multipart: true})
            ).use(
                ProxyAgent( this.proxyMap )
            );

        if ( this.XDomain )  this.core.use( CORS() );

        this.core.use( Static( this.staticPath ) );
    }

    /**
     * Origin URI
     *
     * @type {string}
     */
    get URL() {

        const address = this.address;

        return  address ? `http://${address.address}:${address.port}` : '';
    }

    /**
     * URL to open in default browser
     *
     * @type {string}
     */
    get openURL() {

        return  (typeof this.openPath !== 'string')  ?
            this.URL  :  resolve(this.URL, this.openPath);
    }

    /**
     * Create a server in the same Node.JS process
     *
     * @return {Server} HTTP server
     */
    localHost() {

        const server = this;

        return  this.core.listen(this.netPort,  async function () {

            server.address = Object.assign(this.address(), {
                family:     'IPv4',
                address:    IP.v4.sync()
            });

            if ( process.send )
                return  process.send({type: 'ready', data: server.address});

            console.info(`Web server runs at ${server.URL}`);

            if ( server.openPath )  await open( server.openURL );

        }).on('error',  (error) => {

            if ( process.send )
                process.send({
                    type:    'error',
                    data:    error
                });
            else
                console.error( error );
        });
    }

    /**
     * Boot a server in a forked Node.JS process
     *
     * @return {Promise<ServerAddress>}
     */
    workerHost() {

        const child = fork(
            join(currentModulePath(), '../../dist/koapache-cli'),
            [this.staticPath,  '-p',  this.netPort,  this.XDomain && '--CORS'],
            {execArgv:  [ ]}
        );

        return  new Promise((resolve, reject) => {

            child.on('message',  event => {

                switch ( event.type ) {
                    case 'ready':    return  resolve( event.data );
                    case 'error':    {

                        let error = event.data;

                        reject(Object.assign(
                            new global[ error.name ]( error.message ),  error
                        ));
                    }
                }
            });
        });
    }
}

